    async function fecha_hora_ingreso() {
        // Varibales
        let txtfecha_hora_ingreso = document.getElementById('txtfecha_hora_ingreso');

        // Condiciones
        if (txtfecha_hora_ingreso.value == '21/07/1988 21:00:00' || txtfecha_hora_ingreso.value == '') {
            // Obtén la fecha y hora actual
            let fecha = new Date();

            // Obtiene los componentes de la fecha y hora
            let dia = String(fecha.getDate()).padStart(2, '0');
            let mes = String(fecha.getMonth() + 1).padStart(2, '0');
            let año = fecha.getFullYear();
            let hora = String(fecha.getHours()).padStart(2, '0');
            let minutos = String(fecha.getMinutes()).padStart(2, '0');
            let segundos = String(fecha.getSeconds()).padStart(2, '0');

            // Formatea la fecha y hora en el formato deseado
            let fechaHoraFormateada = dia + '/' + mes + '/' + año + ' ' + hora + ':' + minutos + ':' + segundos;

            // Envio fecha al campo
            txtfecha_hora_ingreso.value = fechaHoraFormateada;
        }
    }

