                    for (let i = 0; i < plant_FO.length; i++) {
                        options_plantillas = `<option value="${plant_FO[i].name_plant}">${plant_FO[i].obs_plant}</option>`;
                        crud_plantillas.innerHTML += options_plantillas;
                    }
                    // Buscar el valor en el array plant_FO
                    const foundPlantFO = plant_FO.find(plant => plant.name_plant === document.getElementById('txtplantilla').value);

                    // Si no se encuentra el valor en plant_FO, establecer el valor de txtplantilla en vacío
