    // * CONTROLAR Down Time TIEMPO TRANSCURRIDO DESDE LA APERTURA DEL CASO
    document.getElementById('txttiempo_duracion_caso').addEventListener('focus', ctrl_tiempo_duracion_caso);    
    async function ctrl_tiempo_duracion_caso() {
        // Variables
        let txttiempo_duracion_caso = document.getElementById("txttiempo_duracion_caso");
        // Obtener valor del campo txtfecha_hora_ingreso
        let txtfecha_hora_ingreso = document.getElementById('txtfecha_hora_ingreso').value;
        let fecha_hora_ingreso = moment(txtfecha_hora_ingreso, "DD/MM/YYYY HH:mm:ss");

        // Obtener la hora actual
        let hora_actual = moment();

        // Calcular la diferencia de tiempo en milisegundos
        let diferencia_milisegundos = hora_actual.diff(fecha_hora_ingreso);

        // Calcular las horas, minutos y segundos de la diferencia de tiempo
        let horas = Math.floor(diferencia_milisegundos / 3600000);
        let minutos = Math.floor((diferencia_milisegundos % 3600000) / 60000);
        let segundos = Math.floor((diferencia_milisegundos % 60000) / 1000);

        // Formatear la diferencia de tiempo en horas, minutos y segundos
        let diferencia_formateada = ("0" + horas).slice(-2) + ":" + ("0" + minutos).slice(-2) + ":" + ("0" + segundos).slice(-2);

        // Envio TMO al campo
        txttiempo_duracion_caso.value = diferencia_formateada;
        txttiempo_duracion_caso.style.border = "1px solid #1e91d7";
        txttiempo_duracion_caso.style.backgroundColor = "#fff";
    }

